package vmhooksgenerate

import (
	"bufio"
	"fmt"
	"os"
)

// WriteEVMOpcodeCost generates EVM code for evm/interpreter/gas_config.go
func WriteEVMOpcodeCost(out *eiGenWriter) {
	out.WriteString(`// Code generated by vmhooks generator. DO NOT EDIT.

// !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
// !!!!!!!!!!!!!!!!!!!!!! AUTO-GENERATED FILE !!!!!!!!!!!!!!!!!!!!!!
// !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!

`)
	out.WriteString("package evm\n\n")
	out.WriteString("type GasConfig struct {\n")

	readFile, err := os.Open("generate/cmd/input/evm_opcodes.txt")
	if err != nil {
		panic(err)
	}
	defer readFile.Close()

	fileScanner := bufio.NewScanner(readFile)
	fileScanner.Split(bufio.ScanLines)

	for fileScanner.Scan() {
		opcode := fileScanner.Text()
		out.WriteString(fmt.Sprintf("\t%-30suint64\n", opcode))
	}
	out.WriteString("}\n")
}
